import { useState, useCallback } from "react"

export default function Sidebar() {
  let [newMenuItem, setNewMenuItem] = useState("")
  let [menuItems, setMenuItems] = useState(["Home", "About", "Contact"]) // Initial menu items
  let [filter, setFilter] = useState("")

  // Function to add a new menu item
  let addMenuItem = useCallback(() => {
    if (newMenuItem.trim() === "") return // Prevent adding empty items
    setMenuItems((prevItems) => [newMenuItem, ...prevItems])
    setNewMenuItem("") // Clear input after adding
  }, [newMenuItem])

  // Filtered menu items
  let filteredMenuItems = menuItems.filter((item) =>
    item.toLowerCase().includes(filter.toLowerCase())
  )

  return (
    <div>
      <input
        type="text"
        id="newMenuItemValue"
        value={newMenuItem}
        onChange={(event) => setNewMenuItem(event.target.value)}
        placeholder="Add a menu item"
      />
      <br />
      <button onClick={addMenuItem}>Add Item</button>
      <br />
      <input
        id="filter"
        type="text"
        value={filter}
        onChange={(event) => setFilter(event.target.value)}
        placeholder="Filter by..."
      />
      <br />
      <ul>
        {filteredMenuItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  )
}
